sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	//gs.addInfoMessage("Nikhil 1 " + new GlideDateTime() + " //"+current.u_when_needed);
	var rightnow1 = new GlideDateTime();
	var whenneeded1 = new GlideDateTime(current.u_when_needed);
	//gs.addInfoMessage("Nikhil 1 " + rightnow1);
	//gs.addInfoMessage("Nikhil 2 " + whenneeded1);

	if(whenneeded1.before(rightnow1)){
		gs.addErrorMessage('whenneded cannot be in past.Your transaction will not be saved in database');
		current.setAbortAction(true);
	}
	gs.info("message captured in Needit");
	var today = rightnow1.getLocalDate();
	var istoday = whenneeded1.getLocalDate();

	if(today.compareTo(istoday) == 0){
		gs.addErrorMessage('whenneded cannot be todays date.Your transaction will not be saved in database');
		current.setAbortAction(true);
	}

})(current, previous);
